import React, { useEffect, useRef, useState } from 'react'
import io from 'socket.io-client'

let mediaConstraints = {
  audio: true, // We want an audio track
  video: true // ...and we want a video track
}
const socket = io('http://localhost:8000')

const openMediaDevices = async constraints => {
  return await navigator.mediaDevices.getUserMedia(constraints)
}

let myPeerConnection

function Room() {
  const userVideo = useRef()

  useEffect(() => {
    socket.emit('join room')

    socket.on('firstguy', () => {
      createPeerConnection()
      navigator.mediaDevices.getUserMedia(mediaConstraints).then(streamz => {
        userVideo.current.srcObject = streamz
        streamz
          .getTracks()
          .forEach(track => myPeerConnection.addTrack(track, streamz))
      })
    })

    socket.on('sendicecandidate', candidate => {
      let newcandidate = new RTCIceCandidate(candidate)
      myPeerConnection.addIceCandidate(newcandidate)
    })

    socket.on('sentVideo', negotiation => {
      var localStream = null
      createPeerConnection()
      var desc = new RTCSessionDescription(negotiation)

      myPeerConnection
        .setRemoteDescription(desc)
        .then(() => {
          return navigator.mediaDevices.getUserMedia(mediaConstraints)
        })
        .then(streamz => {
          localStream = streamz
          userVideo.current.srcObject = localStream

          localStream
            .getTracks()
            .forEach(track => myPeerConnection.addTrack(track, localStream))
        })
        .then(() => {
          return myPeerConnection.createAnswer()
        })
        .then(answer => {
          return myPeerConnection.setLocalDescription(answer)
        })
        .then(() => {
          socket.emit('video-answer', myPeerConnection.localDescription)
        })
    })

    function createPeerConnection() {
      myPeerConnection = new RTCPeerConnection({
        iceServers: [{ urls: 'stun:stun1.l.google.com:19302' }]
      })

      myPeerConnection.onicecandidate = function handleICECandidateEvent(
        event
      ) {
        if (event.candidate) {
          socket.emit('new-ice-candidate', event.candidate)
        }
      }
      // myPeerConnection.ontrack = function handleTrackEvent(event) {
      //   document.getElementById('received_video').srcObject = event.streams[0]
      //   document.getElementById('hangup-button').disabled = false
      // }
      myPeerConnection.onnegotiationneeded = function handleNegotiationNeededEvent() {
        myPeerConnection
          .createOffer()
          .then(function(offer) {
            return myPeerConnection.setLocalDescription(offer)
          })
          .then(() => {
            socket.emit('video-offer', myPeerConnection.localDescription)
          })
        //socketio stuff
      }
    }
  }, [])

  return (
    <div>
      <video autoPlay ref={userVideo}></video>
    </div>
  )
}

export default Room
